INSERT INTO `ecs_article` VALUES ('137', '19', '省委常委、组织部长邓向阳到信福小屋调研指导工作', '<p><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; line-height: 25.6000003814697px;\">3月19日，安徽省委常委、组织部长邓向阳来到信福小屋调研指导工作。市委书记张晓麟，省委非公经济和社会组织工委专职副书记唐汝平，市委常委、组织部长王炜，市委秘书长孙军等陪同调研。</span></p><p>&nbsp;</p><p style=\"margin: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, Arial, sans-serif; font-size: 16px; line-height: 25.6000003814697px; box-sizing: border-box !important; word-wrap: break-word !important;\">&nbsp;</p><p style=\"margin: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, Arial, sans-serif; font-size: 16px; line-height: 25.6000003814697px; box-sizing: border-box !important; word-wrap: break-word !important;\">&nbsp;</p><p style=\"margin: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, Arial, sans-serif; font-size: 16px; line-height: 25.6000003814697px; box-sizing: border-box !important; word-wrap: break-word !important;\"><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; line-height: 1.6;\"><img data-s=\"300,640\" data-type=\"jpeg\" data-src=\"http://mmbiz.qpic.cn/mmbiz/qMQP7z0ZxxtYsHDOCD2SQNqZbKIw9HA5qwyHQG4Ax9lbWogVzo84TUWgV5E8rIeLgkQiaOjnWXcZdEHMZOdpkCA/0?wx_fmt=jpeg\" data-ratio=\"0.75\" data-w=\"\" src=\"http://xjd.taomas.cn/includes/ueditor/php/../../../images/image/64881461927275.png\" style=\"margin: 0px; padding: 0px; height: auto !important; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; width: auto !important; visibility: visible !important;\" alt=\"\"/><br style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important;\"/>&nbsp;一行人来到郑蒲港新区姥桥镇姥长村信福小屋服务社，邓向阳详细询问了信福小屋建设运营情况，并和服务社站长夏云飞进行了深入的交流。</span></p><p style=\"margin: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, Arial, sans-serif; font-size: 16px; line-height: 25.6000003814697px; box-sizing: border-box !important; word-wrap: break-word !important;\">&nbsp;<img data-s=\"300,640\" data-type=\"jpeg\" data-src=\"http://mmbiz.qpic.cn/mmbiz/qMQP7z0ZxxuzOyG5OupksXBEI8vHMvZC558Jica0pAes2aOPBlRLY0UCnP7BicBhK4WfNvPhWmhX94aCDlVicEGiaQ/0?wx_fmt=jpeg\" data-ratio=\"0.75\" data-w=\"\" src=\"http://xjd.taomas.cn/includes/ueditor/php/../../../images/image/2811461927275.png\" style=\"margin: 0px; padding: 0px; height: auto !important; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; line-height: 1.6; width: auto !important; visibility: visible !important;\" alt=\"\"/><span style=\"margin: 0px; padding: 0px; max-width: 100%; box-sizing: border-box !important; word-wrap: break-word !important; line-height: 1.6;\">邓向阳指出，信福小屋平台作为马鞍山市政府信息化工程的一个重要项目，需要整合社会资源，为居民提供一站式服务，居民足不出户就可以收发快递、小额存储贷、办理手机、水电等各种业务缴费；不会上网购物的居民还可以通过小屋进行网络代购，购买各种生活必需品，在各个方面确确实实方便了群众，让群众在家门口享受到了便捷高效的服务。同时对于我市发挥互联网优势，采取市场化运作模式，为农民提供便民服务，发展农村电商，促进农民增收的做法给予充分肯定。 &nbsp;</span></p><p style=\"margin: 0px; padding: 0px; max-width: 100%; clear: both; min-height: 1em; white-space: pre-wrap; color: rgb(62, 62, 62); font-family: &#39;Helvetica Neue&#39;, Helvetica, &#39;Hiragino Sans GB&#39;, &#39;Microsoft YaHei&#39;, Arial, sans-serif; font-size: 16px; line-height: 25.6000003814697px; box-sizing: border-box !important; word-wrap: break-word !important;\">&nbsp; &nbsp;<br/></p><p><br/></p>', '', '', '', '0', '1', '0', '1461569311', '', '0', 'http://', '公告测试7');
